import React, { useState } from 'react';

export default function GameStatsForm() {
  // 初始化表單資料，包含所有 52 個欄位
  const [formData, setFormData] = useState({
    blueChampionKill: '',
    blueTowerKill: '',
    blueInhibitorKill: '',
    blueBaronKill: '',
    blueRiftHeraldKill: '',
    blueTotalGold: '',
    blueMinionsKilled: '',
    blueJungleMinionsKilled: '',
    blueAvgPlayerLevel: '',
    redChampionKill: '',
    redTowerKill: '',
    redInhibitorKill: '',
    redBaronKill: '',
    redRiftHeraldKill: '',
    redTotalGold: '',
    redMinionsKilled: '',
    redJungleMinionsKilled: '',
    redAvgPlayerLevel: '',
    blueDragonKill: '',
    blueDragonHextechKill: '',
    blueDragonChemtechKill: '',
    blueDragonFireKill: '',
    blueDragonAirKill: '',
    blueDragonEarthKill: '',
    blueDragonWaterKill: '',
    blueDragonElderKill: '',
    blueVoidgrubKill: '',
    blueAtakhanKill: '',
    redDragonKill: '',
    redDragonHextechKill: '',
    redDragonChemtechKill: '',
    redDragonFireKill: '',
    redDragonAirKill: '',
    redDragonEarthKill: '',
    redDragonWaterKill: '',
    redDragonElderKill: '',
    redVoidgrubKill: '',
    redAtakhanKill: '',
    redDragonChemtech_DragonKill: '',
    redDragonEarth_DragonKill: '',
    blueDragonHextech_DragonKill: '',
    redDragonHextech_DragonKill: '',
    redDragonWater_DragonKill: '',
    blueDragonFire_DragonKill: '',
    blueDragonChemtech_DragonKill: '',
    redDragonAir_DragonKill: '',
    blueDragonWater_DragonKill: '',
    redDragonFire_DragonKill: '',
    blueDragonEarth_DragonKill: '',
    blueDragonAir_DragonKill: '',
    redDragonElder_DragonKill: '',
    blueDragonElder_DragonKill: ''
  });

  // 處理使用者輸入時更新表單資料
  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  // 表單送出時，將資料傳送到後端 API
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/api/analyze', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });
      const result = await response.json();
      console.log('後端回傳結果:', result);
      alert(JSON.stringify(result, null, 2)); // 顯示後端回傳結果
    } catch (error) {
      console.error('發送請求失敗:', error);
      alert('發送請求失敗，請檢查後端服務。');
    }
  };

  // 產生每個輸入欄位的渲染元件
  const renderInput = (label, name) => (
    <div key={name} style={{ marginBottom: '8px' }}>
      <label>
        {label}：
        <input
          type="number"
          name={name}
          value={formData[name]}
          onChange={handleChange}
          style={{ marginLeft: '8px', width: '100px' }}
        />
      </label>
    </div>
  );

  return (
    <div style={{ padding: '20px', maxWidth: '600px', margin: '0 auto' }}>
      <h2 style={{ textAlign: 'center' }}>LoL 資料分析表單</h2>
      <form onSubmit={handleSubmit}>
        {/* 產生所有欄位的輸入框 */}
        {Object.keys(formData).map((key) =>
          renderInput(key, key)
        )}
        <div style={{ textAlign: 'center', marginTop: '20px' }}>
          <button type="submit" style={{
            padding: '10px 20px',
            backgroundColor: '#4CAF50',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer',
            fontSize: '16px'
          }}>
            開始分析
          </button>
        </div>
      </form>
    </div>
  );
}
