import React, { useState } from 'react';

export default function GameStatsForm() {
  const [formData, setFormData] = useState({
    blueChampionKill: '',
    blueTowerKill: '',
    blueInhibitorKill: '',
    blueBaronKill: '',
    blueRiftHeraldKill: '',
    blueTotalGold: '',
    blueMinionsKilled: '',
    blueJungleMinionsKilled: '',
    blueAvgPlayerLevel: '',
    redChampionKill: '',
    redTowerKill: '',
    redInhibitorKill: '',
    redBaronKill: '',
    redRiftHeraldKill: '',
    redTotalGold: '',
    redMinionsKilled: '',
    redJungleMinionsKilled: '',
    redAvgPlayerLevel: '',
    blueDragonKill: '',
    blueDragonHextechKill: '',
    blueDragonChemtechKill: '',
    blueDragonFireKill: '',
    blueDragonAirKill: '',
    blueDragonEarthKill: '',
    blueDragonWaterKill: '',
    blueDragonElderKill: '',
    redDragonKill: '',
    redDragonHextechKill: '',
    redDragonChemtechKill: '',
    redDragonFireKill: '',
    redDragonAirKill: '',
    redDragonEarthKill: '',
    redDragonWaterKill: '',
    redDragonElderKill: '',
    blueVoidgrubKill: '',
    blueAtakhanKill: '',
    redVoidgrubKill: '',
    redAtakhanKill: '',
    redDragonChemtech_DragonKill: '',
    redDragonEarth_DragonKill: '',
    blueDragonHextech_DragonKill: '',
    redDragonHextech_DragonKill: '',
    redDragonWater_DragonKill: '',
    blueDragonFire_DragonKill: '',
    blueDragonChemtech_DragonKill: '',
    redDragonAir_DragonKill: '',
    blueDragonWater_DragonKill: '',
    redDragonFire_DragonKill: '',
    blueDragonEarth_DragonKill: '',
    blueDragonAir_DragonKill: '',
    redDragonElder_DragonKill: '',
    blueDragonElder_DragonKill: ''
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/api/analyze', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      });
      const responseData = await response.json();
      console.log('Backend Result:', responseData);
      setResult(responseData); // 設定結果
    } catch (error) {
      console.error('Sending request failed:', error);
      setResult({ error: 'Sending request failed, please check backend. ' });
    }
  };

  const groupStyle = {
    border: '1px solid #a88b4d',
    borderRadius: '8px',
    padding: '12px',
    marginBottom: '20px',
    background: '#111827'
  };

  const groupTitleStyle = {
    fontSize: '18px',
    color: '#c7a560',
    marginBottom: '10px',
    borderBottom: '1px solid #a88b4d',
    paddingBottom: '4px',
    fontWeight: 'bold'
  };

  const labelStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    marginBottom: '8px',
    fontFamily: 'Verdana, sans-serif'
  };

  const inputStyle = {
    width: '80px',
    padding: '6px',
    marginLeft: '10px',
    border: '1px solid #a88b4d',
    borderRadius: '4px',
    backgroundColor: '#1e2a44',
    color: '#e0d7c6'
  };

  const buttonStyle = {
    backgroundColor: '#a88b4d',
    color: '#0a0f1c',
    border: 'none',
    padding: '10px 20px',
    borderRadius: '6px',
    cursor: 'pointer',
    fontWeight: 'bold',
    fontSize: '16px',
    boxShadow: '0 0 10px #a88b4d',
    transition: 'all 0.3s ease'
  };

  const buttonHoverStyle = {
    backgroundColor: '#c7a560',
    boxShadow: '0 0 20px #c7a560'
  };

  const [hover, setHover] = useState(false);
  const [result, setResult] = useState(null);

  const renderInput = (label, name) => (
    <div key={name} style={labelStyle}>
      <label htmlFor={name}>{label}：</label>
      <input
        type="number"
        name={name}
        id={name}
        value={formData[name]}
        onChange={handleChange}
        style={inputStyle}
      />
    </div>
  );

  return (
    <div style={{ background: '#0a0f1c', color: '#e0d7c6', padding: '20px', maxWidth: '700px', margin: '0 auto', borderRadius: '10px', border: '2px solid #a88b4d', boxShadow: '0 0 20px rgba(168,139,77,0.5)' }}>
      <h2 style={{ textAlign: 'center', color: '#a88b4d', fontFamily: 'Georgia, serif' }}>LoL Match Data Analysis</h2>
      <form onSubmit={handleSubmit}>
        <div style={groupStyle}>
          <div style={groupTitleStyle}>Blue Side Data</div>
          {['Blue Side Champion Kills', 'Blue Side Tower Kills', 'Blue Side Inhibitor Kills', 'Blue Side Baron Kills', 'Blue Side Rift Herald Kills', 'Blue Side Total Gold', 'Blue Side Minions Killed', 'Blue Side Jungle Minions Killed', 'Blue side Average Player Level'].map((key) =>
            renderInput(key, key)
          )}
        </div>

        <div style={groupStyle}>
          <div style={groupTitleStyle}>Red Side Data</div>
          {['Red Side Champion Kills', 'Red Side Tower Kills', 'Red Side Inhibitor Kills', 'Red Side Baron Kills', 'Red Side Rift Herald Kills', 'Red Side Total Gold', 'Red Side Minions Killed', 'Red Side Jungle Minions Killed', 'Red Side Average Player Level'].map((key) =>
            renderInput(key, key)
          )}
        </div>

        <div style={groupStyle}>
          <div style={groupTitleStyle}>Blue Side Dragon</div>
          {['blueDragonKill', 'blueDragonHextechKill', 'blueDragonChemtechKill', 'blueDragonFireKill', 'blueDragonAirKill', 'blueDragonEarthKill', 'blueDragonWaterKill', 'blueDragonElderKill'].map((key) =>
            renderInput(key, key)
          )}
        </div>

        <div style={groupStyle}>
          <div style={groupTitleStyle}>Red Side Dragon</div>
          {['redDragonKill', 'redDragonHextechKill', 'redDragonChemtechKill', 'redDragonFireKill', 'redDragonAirKill', 'redDragonEarthKill', 'redDragonWaterKill', 'redDragonElderKill'].map((key) =>
            renderInput(key, key)
          )}
        </div>

        <div style={groupStyle}>
          <div style={groupTitleStyle}>Blue Side Rift Herald and Baron Nashor</div>
          {['blueVoidgrubKill', 'blueAtakhanKill'].map((key) =>
            renderInput(key, key)
          )}
        </div>

        <div style={groupStyle}>
          <div style={groupTitleStyle}>Red Side Rift Herald and Baron Nashor</div>
          {['redVoidgrubKill', 'redAtakhanKill'].map((key) =>
            renderInput(key, key)
          )}
        </div>

        <div style={groupStyle}>
          <div style={groupTitleStyle}>Dragon Kills by Type</div>
          {[
            'redDragonChemtech_DragonKill', 'redDragonEarth_DragonKill', 'blueDragonHextech_DragonKill',
            'redDragonHextech_DragonKill', 'redDragonWater_DragonKill', 'blueDragonFire_DragonKill',
            'blueDragonChemtech_DragonKill', 'redDragonAir_DragonKill', 'blueDragonWater_DragonKill',
            'redDragonFire_DragonKill', 'blueDragonEarth_DragonKill', 'blueDragonAir_DragonKill',
            'redDragonElder_DragonKill', 'blueDragonElder_DragonKill'
          ].map((key) =>
            renderInput(key, key)
          )}
        </div>

        <div style={{ textAlign: 'center', marginTop: '20px' }}>
          <button
            type="submit"
            style={hover ? { ...buttonStyle, ...buttonHoverStyle } : buttonStyle}
            onMouseEnter={() => setHover(true)}
            onMouseLeave={() => setHover(false)}
          >
            Start Analysis
          </button>
        </div>
      </form>


      {/* 結果區塊 */}
      {result && (
        <div style={{ marginTop: '30px', padding: '15px', backgroundColor: '#1e2a44', borderRadius: '8px', textAlign: 'center', border: '1px solid #a88b4d' }}>
          {result.error ? (
            <p style={{ color: 'red' }}>Error: {result.error}</p>
          ) : (
            <>
              <p style={{ fontSize: '18px', color: '#c7a560', fontWeight: 'bold' }}>{result.winner}</p>
              <p>Blue Side Win Rate: {result.blue_win_rate}</p>
              <p>Red Side Win Rate: {result.red_win_rate}</p>
            </>
          )}
        </div>
      )}
    </div>
  );
}
